mapper.update:
update water

    water_num = water_num + #{water_num}

where usr_id = #{userId}

UPDATE weight
SET
    usr_weight = #{weight}
WHERE
    usr_id = #{userId}
  AND date = #{time}

UPDATE sport
SET consume = #{sport},
    step = #{step}
WHERE
    usr_id = #{userId}
  AND date = #{date}

mapper.select:
SELECT *
FROM usr
WHERE usr_id = #{usrId}
