mapper.update:
update water

    water_num = water_num + #{water_num}

where usr_id = #{userId}

UPDATE weight
SET
    usr_weight = #{weight}
WHERE
    usr_id = #{userId}
    AND date = #{time}

update cal

    cal_num = cal_num + #{cal_num}

where usr_id = #{userId}

mapper.select:
SELECT *
FROM usr
WHERE usr_id = #{usrId}

SELECT *
FROM cal
WHERE usr_id = #{usrId}
